import React ,{useState} from "react"
import "../styling/examSearchBar.css"
import SearchIcon from '@mui/icons-material/Search';
import Exams from "./Exams";

function ExamSearchBar({placeholder,data}){

  

    const SearchExams=()=>{
        let filter=document.getElementById("myInput").value.toUpperCase();

        let ul=document.getElementById("myUL");
        let li=ul.getElementsByTagName("li");
        for(var i=0;i<li.length;i++){

            let a=li[i].getElementsByTagName('a')[0];
            let textValue=a.textContent|| a.innerHTML;
            if(textValue.toUpperCase().indexOf(filter)>-1 ){
                li[i].style.display="";

            }else{
                li[i].style.display="none";
            }

        }

    }


    
    return(
        <div className="exam_search">
            <div className="exam_search_input">
                <input  type="text"  id="myInput" placeholder={placeholder} onKeyUp={SearchExams} className="exam_input"/>
                <div className="exam_search_icon"><SearchIcon/></div>
            </div>
            <div className="exam_data_result">
                <ul id="myUL">
                    <li><a href="#ias" target="_blank">IAS</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">CLAT</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">CAT</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">UCEED</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">NDA</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">CA</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">IES</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">NID</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">NIFT</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">GATE</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">AIIMS</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">BITSAT</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">JIPMER</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">MANIPAL</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">NAVY</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">ARMY</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">AIEED</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">HSEE</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">JNU</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">HOTEL MANAGEMENT</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">AILET</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">KVPY</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">NEST</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">BHU</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">BANASTHALI</a></li>
                    <li><a href="https://en.wikipedia.org/wiki/Common_Law_Admission_Test">IIT</a></li>
                    
                    
                </ul>
                
            </div>
        </div>

    );
}

export default ExamSearchBar;
